import React, { useState } from 'react';
import './ar.css'

function Ar() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const [stream, setStream] = useState(null);
  const [audio, setAudio] = useState(null);
  const [mixedStream, setMixedStream] = useState(null);
  const [chunks, setChunks] = useState([]);
  const [recorder, setRecorder] = useState(null);
  const [startButton, setStartButton] = useState(null);
  const [stopButton, setStopButton] = useState(null);
  const [downloadButton, setDownloadButton] = useState(null);
  const [recordedVideo, setRecordedVideo] = useState(null);

  const setupStream = async () => {
    try {
      const screenStream = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const audioStream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          sampleRate: 44100,
        },
      });

      setStream(screenStream);
      setAudio(audioStream);
      setupVideoFeedback(screenStream);
    } catch (error) {
      console.error(error);
    }
  };

  const setupVideoFeedback = (stream) => {
    if (stream) {
      const video = document.querySelector('.video-feedback');
      video.srcObject = stream;
      video.play();
    } else {
      console.warn('No stream available');
    }
  };

  const startRecording = async () => {
    await setupStream();

    if (stream && audio) {
      const mixedStream = new MediaStream([...stream.getTracks(), ...audio.getTracks()]);
      const mediaRecorder = new MediaRecorder(mixedStream);
      mediaRecorder.ondataavailable = handleDataAvailable;
      mediaRecorder.onstop = handleStop;
      mediaRecorder.start(1000);

      setRecorder(mediaRecorder);
      setStartButton(true);
      setStopButton(false);

      console.log('Recording started');
    } else {
      console.warn('No stream available.');
    }
  };

  const stopRecording = () => {
    recorder.stop();
    setStartButton(false);
    setStopButton(true);
  };

  const handleDataAvailable = (e) => {
    setChunks([...chunks, e.data]);
  };

  const handleStop = (e) => {
    const blob = new Blob(chunks, { type: 'video/mp4' });
    setChunks([]);

    const videoUrl = URL.createObjectURL(blob);
    setDownloadButton(videoUrl);
    setRecordedVideo(videoUrl);

    const recordedVideoElement = document.querySelector('.recorded-video');
    recordedVideoElement.load();
    recordedVideoElement.onloadeddata = () => {
      const recordedVideoWrap = document.querySelector('.recorded-video-wrap');
      recordedVideoWrap.classList.remove('hidden');
      recordedVideoWrap.scrollIntoView({ behavior: 'smooth', block: 'start' });

      recordedVideoElement.play();
    };

    stream.getTracks().forEach((track) => track.stop());
    audio.getTracks().forEach((track) => track.stop());

    console.log('Recording stopped');
  };

  React.useEffect(() => {
    setStartButton(document.querySelector('.start-recording'));
    setStopButton(document.querySelector('.stop-recording'));
    setDownloadButton(document.querySelector('.download-video'));
    setRecordedVideo(document.querySelector('.recorded-video'));
  }, []);


  return (
    <div className="App">
      <div id="content">
        {/* Uncomment this section to re-enable the original menu
        <div id="mySidenav" className={`sidenav ${menuOpen ? 'open' : ''}`}>
          <a href="javascript:void(0)" className="closebtn" onClick={closeNav}>&times;</a>
          <a className="ar-object" id="1" href="#">
            <img src="./pics/1.png" alt="" style={{ width: 90 }} />
          </a>
          <a className="ar-object" id="2" href="#">Item_2</a>
          <a className "ar-object" id="3" href="#">Item_3</a>
          <a className="ar-object" id="4" href="#">Item_4</a>
          <a className="ar-object" id="5" href="#">Item_5</a>
          <a className="ar-object" id="7" href="#">Item_7</a>
        </div>
        */}

        {/* <nav className={`navbar ${menuOpen ? 'open' : ''}`}>
          <span className="closebtn" onClick={toggleMenu}>&times;</span>
          <ul>
            <li><a className="ar-object" id="2" href="#">Item_2</a></li>
            <li><a className="ar-object" id="3" href="#">Item_3</a></li>
            <li><a className="ar-object" id="4" href="#">Item_4</a></li>
            <li><a className="ar-object" id="5" href="#">Item_5</a></li>
          </ul>
        </nav> */}

